import React,{useStat, useEffect, useState} from 'react'
import styled from 'styled-components';
import Logo from "../assets/logo"

function Contacts({ contacts, currentUser }) {
const [currentUserName, setCurrentUserName] = useState(undefined);
const [currentUserImage, setCurrentUserImage] = useState(undefined);
 const [currentSelected, setCurrentSelected] = useStat(undefined); 

useEffect(()=>{
    if(currentUser){
        setCurrentUserImage(currentUser.avatarImage);
        setCurrentUserName(currentUser.username);
    }
}, [currentUser]);

const chnageCurrentChat = (index, contact) => {};



  return <>
   {
    currentUserImage && currentUserName && (
        <Container>
            <div className="brand">
                <img src={Logo} alt="logo" />
                <h3>MingleChat</h3>
            </div>
            <div className="contacts">
                {
                    contacts.map((contact, index)=>{
                        return(
                            <div key={index} className={`contact ${index === currentSelected ? "selected" : ""}`}>
                                 <div className="avatar">
                                    <img src={`data:image/svg+xml;base64,${contact.avatarImage}`} alt="avatar"   />
                                 </div>
                                 <div className="username">
                                    <h3>{contact.username}</h3>
                                 </div>
                            </div>
                        )
                })}
            </div>
        </Container>
    )
   }
    </>

}

const Container = styled.div``;

export default Contacts;
