avatars.map((avatar, index) => {
  return(
    <div key={index} className={`avatar ${selectedAvatar === index ? "selected" : ""}`}>

      <img src={`data:image/svg+xml;base64,${avatar}`} alt="avatar" onClick={() => setSelectedAvatar} />
    </div>
  )
})
